bool selectSwitch(){
	volatile int* sw_ptr = (int*)0xFF200040;
	int value = *sw_ptr;
	value = value&0b111111111;
	if(value==0b0){
		return false;
	}
	else{
	return true;
	}
}

void pushButtons(){
volatile int* buttonptr = (int*)0xFF20005C;
	volatile int* led = (int*)0xFF200000;
	
	int x = 0;
	int y = 0;

	
	while(1){
		int value = *buttonptr;
		value = value&0b1111;
		switch(value){
			case 0b0001: //move right
				*led = value;
				if(x!=7){ //as long as in bounds
					x++;
					drawSquare(x,y,RED);
				}
				*buttonptr = value;
				break; //exit while loop
			
			case 0b0010: //move down
				if(y!=7){
					y++;
					drawSquare(x,y,RED);
				}
				*led = value;
				*buttonptr = value;
				break; //exit while loop
			
			case 0b0100: //move up
				*led = value;
				if(y!=0){
					y--;
					drawSquare(x,y,RED);
				}
				*buttonptr = value;
				break; //exit while loop
			
			case 0b1000: //move left
				*led = value;
				if(x!=0){
					x--;
					drawSquare(x,y,RED);
				}
				*buttonptr = value;
				break; //exit while loop
		}
		
		/*if(value && 0b1){
			*led = value;
		}*/
	}
}

void printBoard(){
	///printing the board with the pieces can wrap into a function if you want
	for(int x = 0; x < 8; x++){
		for(int y = 0; y < 8; y++){
			if(board[x][y][1]!=-1){//if not empty square
				switch(board[x][y][1]){//check unit
					case 0: // pawns
						if(board[x][y][4]==0){//white grid
							if(board[x][y][0]==0){//white pawn
								whitepawnlightbg(x,y);
							}else{
							blackpawnlightbg(x,y);//whitegridblackpawn
							}
						}else{//black grid
							if(board[x][y][0]==0){//white pawn
								whitepawndarkbg(x,y);
							}else{
							blackpawndarkbg(x,y);//blackgridblackpawn
							}
						}
						break;
					case 1: // rook
						if(board[x][y][4]==0){//white grid
							if(board[x][y][0]==0){//white pawn
								whiterooklightbg(x,y);
							}else{
							blackrooklightbg(x,y);//whitegridblackpawn
							}
						}else{//black grid
							if(board[x][y][0]==0){//white pawn
								whiterookdarkbg(x,y);
							}else{
							blackrookdarkbg(x,y);//blackgridblackpawn
							}
						}
						break;
					case 2: // knight
						if(board[x][y][4]==0){//white grid
							if(board[x][y][0]==0){//white pawn
								whiteknightlightbg(x,y);
							}else{
							blackknightlightbg(x,y);//whitegridblackpawn
							}
						}else{//black grid
							if(board[x][y][0]==0){//white pawn
								whiteknightdarkbg(x,y);
							}else{
							blackknightdarkbg(x,y);//blackgridblackpawn
							}
						}
						break;
					case 3: // bishop
						if(board[x][y][4]==0){//white grid
							if(board[x][y][0]==0){//white pawn
								whitebishoplightbg(x,y);
							}else{
							blackbishoplightbg(x,y);//whitegridblackpawn
							}
						}else{//black grid
							if(board[x][y][0]==0){//white pawn
								whitebishopdarkbg(x,y);
							}else{
							blackbishopdarkbg(x,y);//blackgridblackpawn
							}
						}
						break;
					case 4: // queen
						if(board[x][y][4]==0){//white grid
							if(board[x][y][0]==0){//white pawn
								whitequeenlightbg(x,y);
							}else{
							blackqueenlightbg(x,y);//whitegridblackpawn
							}
						}else{//black grid
							if(board[x][y][0]==0){//white pawn
								whitequeendarkbg(x,y);
							}else{
							blackqueendarkbg(x,y);//blackgridblackpawn
							}
						}
						break;
					case 5: // king
						if(board[x][y][4]==0){//white grid
							if(board[x][y][0]==0){//white pawn
								whitekinglightbg(x,y);
							}else{
							blackkinglightbg(x,y);//whitegridblackpawn
							}
						}else{//black grid
							if(board[x][y][0]==0){//white pawn
								whitekingdarkbg(x,y);
							}else{
							blackkingdarkbg(x,y);//blackgridblackpawn
							}
						}
						break;
					default:
						break;
				}
			}
		}
	}

}

